
public class Pattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	int y=1 , x=2,z= 1,a=3;
	for(int i=0;i<3;i++)
	{
		for(int k=0;k<x;k++)
		{
			System.out.print(" ");
		}
		for(int j=0;j<y;j++)
		{
			System.out.print("*");
			
		}
		System.out.print("\n");
		y=y+2;
		x=x-1;
	}
	for(int m=0;m<2;m++)
	{
		for(int n=0;n<z;n++)
		{
			System.out.print(" ");
		}
		for(int o=0;o<a;o++)
		{
			System.out.print("*");
		}
		System.out.print("\n");
		z++;
		a=a-2;
	}
	
	//for(int )
	
	}
	

}
